import React from "react";
import "./style.css";
import { Col } from "../components/Grid";

class WritingArea extends Component {
  render() {
    return (
      <>
        <Col size="12">
          <div className="complete-story-text">{this.props.storytext}</div>
        </Col>
      </>
    );
  }
}

export default WritingArea;
